import type { TKukarachaType } from "models/kukaracha/lib"
import { memo } from "react"

interface KukarachaImageProps {
    type?: TKukarachaType
    color?: string
}

export const KukarachaImage = memo(({ type = "default", color = "#000" }: KukarachaImageProps) => {
    const KukaImg = generateKukaracha({ type, color })

    return (
        <div className="relative p-[27px] bg-white rounded-xl flex items-center justify-center ">
            {KukaImg}
        </div>
    )
})

KukarachaImage.displayName = "KukarachaImage"

